mapper.update:
update t_analysis_updatelog
set update_time = #{updateTime,jdbcType=TIMESTAMP},
  word_add = #{wordAdd,jdbcType=BIGINT},
  title_add = #{titleAdd,jdbcType=BIGINT},
  needtime = #{needtime,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, update_time, word_add, title_add, needtime
from t_analysis_updatelog
where id = #{id,jdbcType=BIGINT}
